* { box-sizing: border-box; }

body { background-color: #001f3f; margin: 0; display: flex; flex-direction: row; font-family: "Roboto", sans-serif; }

header { min-height: 32px; background: #0074d9; color: white; width: 100%; display: flex; text-align: center; justify-content: center; align-items: center; position: relative; z-index: 2; }
header a { color: white; text-decoration: none; }

footer { min-height: 200px; background: #0074d9; color: #ffffff; display: flex; margin: 0; padding: 0; width: 100%; padding: 1em; display: flex; flex-direction: column; }
footer a { color: #FFDC00; }

main { flex-grow: 1; display: flex; flex-direction: column; align-items: center; overflow: auto; height: 100vh; }
main .secret-menu { display: none; position: fixed; color: #FFDC00; padding: .6em; height: 36px; width: 36px; justify-content: center; align-items: center; background: #85144b; top: 36px; right: 36px; z-index: 1; font-size: 3em; border-radius: 3em; }
main .secret-menu a { color: #FFDC00; }
main article { flex-grow: 1; }
main article, main section { max-width: 720px; padding: 0 1em; width: 100%; background-color: #ffffff; color: #001f3f; }
main article ul, main article pre, main section ul, main section pre { overflow: auto; }
main article ul, main article ol, main section ul, main section ol { margin: 0; }
main article hr, main section hr { border: none; border-top: 1px solid; }
main article kbd, main article pre, main article code, main section kbd, main section pre, main section code { padding: .25em .25em; border-radius: .5em; background: #111111; color: #ffffff; }
main article table, main section table { border: 1px solid; overflow: auto; }
main article table th, main section table th { background: #85144b; color: #ffffff; font-weight: normal; padding: .25em; }
main article table td, main section table td { border: 1px solid #001f3f; }
main article strong, main section strong { font-weight: bold; }
main article pre, main section pre { background: #111111; color: #ffffff; }
main article p, main section p { margin: .5em 0; }
main article h1, main article h2, main article h3, main article h4, main article h5, main article h6, main section h1, main section h2, main section h3, main section h4, main section h5, main section h6 { font-family: "Vesper Libre", cursive; margin-bottom: .125em; margin-top: .75em; line-height: 1em; color: #85144b; }
main article h1, main section h1 { font-size: 3em; }
main article h2, main section h2 { font-size: 2em; }
main article h3, main section h3 { font-size: 1.5em; }
main article h4, main section h4 { font-size: 1.25em; }
main article h5, main section h5 { font-size: 1em; }
main article h6, main section h6 { font-size: .75em; }
main article a, main section a { color: #F012BE; font-style: italic; }
main section.default { flex-grow: 1; width: 100%; }
main section:not(:empty) { padding-bottom: 1em; }

body > nav { background: #85144b; width: 96px; display: flex; flex-direction: column; flex-shrink: 0; align-items: center; position: relative; z-index: 2; }
body > nav a { min-height: 96px; width: 100%; text-decoration: none; display: flex; flex-direction: column; justify-content: center; align-items: center; color: #ffffff; }
body > nav a .fa { font-size: 2em; text-decoration: none; }
body > nav a:hover { background: #001f3f; color: #FFDC00; }
body > nav a.active { background-color: #FFDC00; color: #001f3f; }
body > nav a.active:hover { background: #0074d9; color: #ffffff; }

header.in-body { display: none; }

@media only screen and (max-width: 840px) { body { flex-wrap: wrap; }
  header.in-body { display: flex; }
  main { height: auto; min-height: 100vh; max-width: 100%; overflow: unset; }
  main .secret-menu { display: flex; }
  header.in-main { display: none; } }
@media only screen and (max-width: 840px) { body > nav { width: 100%; flex-direction: row; justify-content: space-around; } }
@media only screen and (max-width: 736px) { main { max-width: 100%; } }

