.category, .tag { background: floralwhite; }

* { box-sizing: border-box; }

body { background-color: #371437; margin: 0; display: flex; flex-direction: row; }

header { min-height: 32px; background: green; border-bottom: 1px solid; width: 100%; display: flex; text-align: center; justify-content: center; align-items: center; }
header a { text-decoration: none; }

footer { min-height: 200px; background: yellow; display: flex; margin: 0; padding: 0; width: 100%; border-top: 1px solid; }

main { flex-grow: 1; display: flex; flex-direction: column; align-items: center; overflow: auto; height: 100vh; }
main article { flex-grow: 1; }
main article, main section { border-right: 1px solid black; border-left: 1px solid black; max-width: 720px; padding: 0 1em; width: 100%; background-color: #dbc7ad; color: #3b0053; }
main article ul, main article pre, main section ul, main section pre { overflow: auto; }
main article hr, main section hr { border: none; border-top: 1px solid; }
main article kbd, main article pre, main section kbd, main section pre { background: rgba(0, 0, 0, 0.15); padding: .75em .75em; border-radius: 0.5em; }
main article table, main section table { border: 1px solid; overflow: auto; }
main article table th, main section table th { background: pink; }
main article table td, main article table th, main section table td, main section table th { border: 1px solid; }
main article pre, main section pre { color: white; }
main section.default { flex-grow: 1; width: 100%; }
main section:not(:empty) { padding-bottom: 1em; }

body > nav { background: darkorange; width: 96px; display: flex; flex-direction: column; border-right: 1px solid; align-items: center; }
body > nav a { min-height: 96px; width: 100%; text-decoration: none; display: flex; flex-direction: column; justify-content: center; align-items: center; color: black; }
body > nav a .fa { font-size: 2em; text-decoration: none; }
body > nav a:hover { background: #0073ff; color: darkorange; }
body > nav a.active { background-color: #ffe8cc; color: darkorange; }
body > nav a.active:hover { background: white; }

header.in-body { display: none; }

@media only screen and (max-width: 840px) { body { flex-wrap: wrap; }
  header.in-body { display: flex; }
  main { height: auto; min-height: 100vh; max-width: 100%; overflow: unset; }
  header.in-main { display: none; } }
@media only screen and (max-width: 834px) { body > nav { width: 100%; flex-direction: row; justify-content: space-around; border-bottom: 1px solid; border-right: none; } }
@media only screen and (max-width: 736px) { main { max-width: 100%; }
  main article, main section { border: none; } }

